import React from 'react';
import '../vibecheck.css';
import { Redirect } from "react-router-dom";
import Select from 'react-select';
import makeAnimated from 'react-select/animated';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const genres = []
const recommended = []
const animatedComponents = makeAnimated();


const Vibes = [
  { label: "Funky", value: "Funky" },
  { label: "Happy", value: "Happy" },
  { label: "Sad", value: "Sad" },
  { label: "Chill", value: "Chill" },
  { label: "Flirty", value: "Flirty" },
  { label: "Study", value: "Study" },
  { label: "Workout", value: "Workout" },
  { label: "Nostalgic", value: "Nostalgic" }
];

class Playlist extends React.Component {


  //the states of emotion and source will be set to null initially until the user had filled out the form.
  constructor() {
    super();
    this.state = {
      data: null,
      genres: null,
      recArtists: null,
      vibe: "Funky"
    };
  }

  componentDidMount() {

    axios.get(`http://localhost:5000/allGenres`)
      .then((response) => {

        for (var i = 0; i < response.data.data.genres.length; i++) {
          genres.push({ label: response.data.data.genres[i], value: response.data.data.genres[i] })
        }

        this.setState({
          genres: response.data.data.genres
        });
      }).catch((error) => {
        alert("There was an error connecting to the api")
        console.error(error);
      });

  }

  handleVibe = newVibe => {
    this.setState({vibe: newVibe['label']})
  };

  handleGenres = genreChange => {
    var string = ""
    var finalGenres = []

    if (genreChange != null) {
      if (genreChange.length >= 3) {
        if(genreChange.length === 3) {

          for (var i = genreChange.length - 3; i < genreChange.length; i++) {
            finalGenres.push(genreChange[i]['value'])
          }

          const request = axios({
              headers: { 
                  'content-type': 'application/json'
              },
              method: 'post',
              url: `http://localhost:5000/recommendations`,
              params: {
                  finalGenres
              }
          })
          .then((response) => {
            var length = (response.data.data.tracks.length)

            var tempArr = []
           /* for (var i = 0; i < response.data.data.genres.length; i++) {
              genres.push({ label: response.data.data.genres[i], value: response.data.data.genres[i] })
            }

            this.setState({
              genres: response.data.data.genres
            });*/
            //alert(response.data.data.tracks[0].artists[0].name)
            for (var i = 0; i < length; i++) {
              recommended.push({label: response.data.data.tracks[i].artists[0].name, value: response.data.data.tracks[i].artists[0].name})
            }
            /*this.setState({
              recArtists: tempArr
            });*/
          }).catch((error) => {
            alert("There was an error connecting to the api")
            console.error(error);
          });
        }
        else {alert("You must ONLY have three genres.")}
      }
    }
  };

  render() {
    if (this.state.genres != null) {
      this.items = this.state.genres.map((item, key) =>
        <option name={item} key={key}>{item}</option>
      );
    }

    if (this.state.recArtists != null) {
      this.artists = this.state.recArtists.map((item, key) =>
        <option name={item} key={key}>{item}</option>
      );
    }

    return (
      <div>
        <form action='http://localhost:5000/newPlaylist' method='POST'>
          <div className="container">
            <div className="row">
              <div className="col-md-3"></div>
              <div className="col-md-6">
                <h1>Select Vibe</h1>
                <Select options={Vibes} onChange={this.handleVibe} components={animatedComponents}
                />
                <br></br>
                <h1>Select Genres</h1>
                <Select options={genres} onChange={this.handleGenres} components={animatedComponents} isMulti />
                <br></br>
                <Select options={recommended} components={animatedComponents} isMulti />
                <label for="artists">Choose three artists:</label>
                <br></br>
                <select name="artists" id="artists" multiple>
                  {this.artists}
                </select>

                <br></br>

              </div>
              <div className="col-md-4"></div>
            </div>
          </div>
        </form>
      </div>
    )
  }
}
export default Playlist;
